import axios from 'axios';
import React, {useState,useEffect} from 'react'
import { Container, Row, Spinner } from 'react-bootstrap';
import Moment from 'react-moment';

function Comments({id}) {
    const [comments, setComments] = useState([]);
    const [isloading, setIsloading] = useState(true);

    useEffect(()=>{
        const getDiseasesInfo = async ()=>{
                const res = await axios.get(`https://lifestylediseases.herokuapp.com/feedbackverify/${id}`);
                setComments(res.data);
                setIsloading(false);
        }

        getDiseasesInfo();
    
        return ()=>{
            setComments([]);
        }
    },[id]);

    if(isloading){
        return(
            <Container style={{height:"100vh"}} className="d-flex justify-content-center align-items-center">
                <Row >
                        <Spinner animation="grow" variant="info" />
                </Row>
            </Container>
        )
    }

    return (
        <div className="commaints">
            <h2><span>{comments.length <=9 ? `0${comments.length}` : comments.length}</span> Comments</h2>
            <div className="comments-row">
                {
                    comments.length !== 0
                    ?
                    comments.map(comment=>(
                        <div className="comments-body">
                            <img src="data:image/png;base64," alt="" />
                            <div>
                                <h3>{comment.name}</h3>
                                <p><Moment fromNow date={comment.feedback_date}/></p>
                                <p>{comment.feedback}</p>
                            </div>
                        </div>
                    ))
                    :
                    <div className="comments-body">
                        <div>
                            <h3 className='text-danger text-center'>Comments Not Founds !</h3>
                        </div>
                    </div>
                }
               
            </div>
        </div>
    )
}

export default Comments
